mapper.select:
  	SELECT COUNT(id) FROM sys_account


    		creator_id = #{accountId}


    		and name = #{name}


    		and account = #{account}



	        and CONCAT(name,account) LIKE #{keyword}


        	and is_del = #{isDel}


        	and role_id = #{roleId}


        	and hospital_id = #{hospitalId}


        	and hospital_group_id = #{hospitalGroupId}

  	SELECT  FROM sys_account


    		creator_id = #{accountId}


    		and name = #{name}


    		and account = #{account}



	        and CONCAT(name,account) LIKE #{keyword}


        	and is_del = #{isDel}


        	and role_id = #{roleId}


        	and hospital_id = #{hospitalId}


        	and hospital_group_id = #{hospitalGroupId}


     ORDER BY id DESC

    	LIMIT #{startIndex},#{pageSize}
